import React, { Component } from "react";
import { RoughNotation } from "react-rough-notation";

class IntroText extends Component {
  constructor(props) {
    super(props);
    this.state = { show: true };
  }

  render() {
    return (
      <div>
        Hi I'm
        <i>
          <RoughNotation type="underline" show={this.state.show}>
            <b> Niels</b>
          </RoughNotation>
        </i>
        ,
        <br />
        And I've recently decided to start my career in the tech space. I've
        succesfully retrained as an Fullstack software Developer
        <b>
          <i> Northcoders Developer Pathway </i>
        </b>
        in
        <i>
          <b> Manchester</b>
        </i>
        . Read more about my starting experience on /my blog/.
      </div>
    );
  }
}

export default IntroText;
