import React from "react";
import Layout from "../components/Layout";
import ProjectText from "./content/ProjectText";
import styles from "./content/content.module.css"
import { getSortedPostsData} from '../lib/posts'
import Link from 'next/link'
import Date from '../components/date'

export async function getStaticProps() {
  const allPostsData = getSortedPostsData()
  const query = "Javascript"
  console.log(query)
  return {
    props: {
      allPostsData,
      query,
    }
  }
}


const blog = ({ allPostsData, query }) => {
    const tagsArr = allPostsData.filter(post => {
    return post.tags.includes(query)
    }
  );
   return (
    <Layout>
    <section>
        <ul>
          {tagsArr.map(({ id, date, title, tags }) => (
            <li key={id}>
              {console.log(id)}
              <Link href="/posts/[id]" as={`/posts/${id}`}>
              <a>{title}</a></Link>
              <br />

            {console.log(tags)}
             <Date dateString={date} />
             <div>{tags.map((tag) => (
          <Link href={"/blog"} as ={"/blog"}><a><button onClick={({tag}) => {getStaticProps()}} key={tag}>{tag}</button></a></Link>
        ))}</div>
            </li>
          ))}
        </ul>
      </section>

    </Layout>
  );
};

export default blog;
