import React from "react";
import Layout from "../components/Layout";
import ProjectText from "./content/ProjectText";
import styles from "./content/content.module.css"
import { getSortedPostsData} from '../lib/posts'
import Link from 'next/link'
import Date from '../components/date'
import { useRouter } from 'next/router'



export async function getStaticProps() {
  const allPostsData = getSortedPostsData()
  const query = ""
  console.log(query)
  return {
    props: {
      allPostsData,
      query,
    }
  }
}



const blog = ({ allPostsData, query }) => {
  const router = useRouter()
console.log(router.query, );
    const tagsArr = allPostsData.filter(post => {
      if (query){
    return post.tags.includes(query)}
    else {return post}
    }
  );
   return (
    <Layout>
    <section>
        <ul>
          {tagsArr.map(({ id, date, title, tags }) => (
            <li key={id}>
              <Link href="/posts/[id]" as={`/posts/${id}`}>
              <a>{title}</a></Link>
              <br />

             <Date dateString={date} />
             <div>{tags.map((tag) => (
          <Link href={{pathname: '/blog', query: { "tag" : tag}}} as ={"/blog"}><a><button>{tag}</button></a></Link>
        ))}</div>
            </li>
          ))}
        </ul>
      </section>

    </Layout>
  );
};

export default blog
