import React, { Component } from 'react';
import Link from "next/link";
import styles from "./navbar.module.css";
import { showDiv, MenuToggle } from '../lib/menu'

const Menu = [
    {
      path: "/blog", name: "Blog",
    },
    {
      path: "/#", name: "100DaysofCode",
    },

    ]


class NavBarState extends Component {
state = {
    mobile_Menu : false
};

openNav = () => {
    console.log(this.state)
  this.setState({
      mobile_Menu : true
  }) };

    render() {
        return (
            <header className={styles.header_Wrapper}>
            <div className={styles.div_top_hypers}>
              <div className={styles.logo_left}>NdV</div>
              <ul className={styles.ul_top_hypers}>
               {Menu.map(({path, name}) => (
                <li key={name}>
                  <Link href={path} as={path}>
                    <a>{name}</a>
                  </Link>
                </li>

               ))} 
                </ul>
                 </div>
                 <div className={styles.menu_right}><Link href={"#"}><button onClick={() => {this.openNav()}}><i className="fa fa-bars"></i></button></Link></div>
              <MenuToggle />
            </header>
        );
    }
}

export default NavBarState;
